import React, { useEffect, useState, useRef } from 'react';

import './index.css';
import wordList from './resources/words.json';

const MAX_TYPED_KEYS = 30;
const WORD_ANIMATION_INTERVAL = 200;

const getWord = () => {
    const index = Math.floor(Math.random() * wordList.length);
    const word = wordList[index];
    return word.toLowerCase();
};

const isValidKey = (key, word) => {
    if (!word) return false;
    const result = word.split('').includes(key);
    return result;
}

const Word = ({ word, validKeys }) => {
    if (!word) return null;
    const joinedKeys = validKeys.join('');
    const matched = word.slice(0, joinedKeys.length);
    const remainder = word.slice(joinedKeys.length);

    const completedClass = (joinedKeys === word) ? 'matched completed' : 'matched';

    return (
        <>
            <span className={completedClass}>{matched}</span>
            <span className="remainder">{remainder}</span>
        </>
    )
}

const App = () => {

    const [typedKeys, setTypedKeys] = useState([]);
    const [validKeys, setValidKeys] = useState([]);
    const [completedWords, setCompletedWords] = useState([]);
    const [word, setWord] = useState('');
    const containerRef = useRef(null);

    useEffect(() => {
        setWord(getWord());
        if(containerRef) containerRef.current.focus();
    }, []);

    let timeout  = null;
    useEffect(() => {
        const wordFromValidKeys = validKeys.join('').toLowerCase();

        if (word && word === wordFromValidKeys) {
            setTimeout(() => {
                let newWord = null;
                do {
                    newWord = getWord();
                } while (completedWords.includes(newWord));

                setWord(newWord);
                setValidKeys([]);
                setCompletedWords((prev) => [...prev, word]);
            }, WORD_ANIMATION_INTERVAL)
        }
        return () => {
            if(timeout) clearTimeout(timeout);
        };

    }, [word, validKeys, completedWords]);

    const handleKeyDown = (e) => {
        e.preventDefault();
        const { key } = e;

        setTypedKeys((prev) => [...prev, key].slice(MAX_TYPED_KEYS * -1));

        if (isValidKey(key, word)) {
            setValidKeys((prev) => {
                const isValidLength = prev.length <= word.length;
                const isNextChar = isValidLength && word[prev.length] === key;

                console.log('prevValueKeys', prev, prev.length);
                console.log('word', word);
                console.log('isNextChar', isNextChar, key);

                return isNextChar ? [...prev, key] : prev;
            });
        }

    }



    return (
        <div className="container" tabIndex="0" onKeyDown={handleKeyDown} ref={containerRef}>
            <div className="valid-keys">
                <Word word={word} validKeys={validKeys} />
            </div>
            <div className="typed-keys">{typedKeys ? typedKeys.join(' ') : null}</div>
            <div className="completed-words">
                <ol>
                    {completedWords.map((word) => (<li key={word}>{word}</li>))}
                </ol>
            </div>
        </div>
    )
}

export default App;
