import React, { useEffect, useState } from 'react';

import './index.css';
import wordList from './resources/words.json';

const MAX_TYPED_KEYS = 30;

const getWord = () => {
    const index = Math.floor(Math.random() * wordList.length);
    const word = wordList[index];
    return word.toLowerCase();
};

const isValidKey = (key, word) => {
    if(!word) return false;
    const result = word.split('').includes(key);
    return result; 
}

const Word = ({word, validKeys}) => {
    if(!word) return null;
    const joinedKeys = validKeys.join('');
    const matched = word.slice(0, joinedKeys.lebgth);
    const remainder = word.slice(joinedKeys.length);


    return(
        <>
            <span className="matched"></span>
            <span className="remainder">{word}</span>
        </>
    )
}

const App = () => {

    const [typedKeys, setTypedKeys] = useState([]);
    const [validKeys, setValidKeys] = useState([]);
    const [word, setWord] = useState('');

    useEffect(() => {
        setWord(getWord());
    }, [])

    const handleKeyDown = (e) => {
        e.preventDefault();
        const { key } = e;

        setTypedKeys((prev) => [...prev, key].slice(MAX_TYPED_KEYS * -1));

        if(isValidKey(key, word)){
            setValidKeys((prev) => {
                const isValidLength = prev.length <= word.length;
                const isNextChar = isValidLength && word[prev.length] === key;

                console.log('prevValueKeys', prev, prev.length);
                console.log('word', word);
                console.log('isNextChar', isNextChar, key);

                return isNextChar ? [...prev, key] : prev; 
            });
        }

        console.log('key', key);

    }

    return (
        <div className="container" tabIndex="0" onKeyDown={handleKeyDown}>
            <div className="valid-keys">
                <Word word={word} validKeys={validKeys} />
            </div>
            <div className="typed-keys">{typedKeys ? typedKeys.join(' ') : null}</div>
            <div className="completed-words">
                <ol>
                    <li>cidade</li>
                    <li>carro</li>
                    <li>profisional</li>
                </ol>
            </div>
        </div>
    )
}

export default App;
